import React from 'react'
import axios from 'axios'
import { useState } from 'react'
// import './CSS/LoginSignUp.css'
import {Link,useNavigate} from 'react-router-dom'
import './Login.css'

function Login() {

  const [values,setValues]= useState({

    email:'',
    fpassword:''

  })

  const navigate = useNavigate();

  function handleSubmit(e){

    e.preventDefault();
     axios.post("http://localhost:4500/login/",values)
    .then(res=>{
      console.log(res);
      if(res.data === "success"){
        
        // console.log(res.json());
         alert("login sucessfull")
        navigate('/qr');
      }
      else{

        alert("no record found");
        console.log(res);
      }

     
    })
    .catch(err=>console.log(err));

  }


  return (
    <div className='loginsignup'>
      
      <div className='login-container'>

      <form onSubmit={handleSubmit}>
        <h1>Login Page</h1>
        <div className='loginsignup-fields'>

         
          <input type='email'name="email" placeholder='Email Address' onChange={e=>setValues({...values,email:e.target.value})}/>
          <input type='password' placeholder='Password' name='fpassword' onChange={e=>setValues({...values,fpassword:e.target.value})}/>
        </div>
        <button>Login</button>
        </form>
        {/* <p className='loginsignup-login'>Already have an account? <span></span></p> */}

        <div className='loginsignip-agree'>
            <input type='checkbox' name='' id=''/>
            <p>By continuing i agree to terms of use & privacy policy.</p>
        </div>

      </div>
    </div>
  )
}

export default Login;
