import axios from "axios";
import React, { useEffect, useState } from "react";
// import SideComp from "./SideComp";
import "./AddData.css";
import moment from "moment";
import Navbar from "./Navbar/Navbar";
import { useParams } from "react-router-dom";

function UpdateCropInfo() {
  const { id } = useParams();
  console.log(id);

  const [data, setData] = useState([]);
  const [state, setState] = useState(data.state);
  const [district, setDistrict] = useState(data.district);
  const [taluka, setTaluka] = useState(data.taluka);
  const [area, setArea] = useState(data.area);
  const [soil, setSoil] = useState(data.soil);
  // const [crop, setCrop] = useState("");
  const [season, setSeason] = useState(data.season);
  const [culti_date, setCultiDate] = useState(data.culti_date);
  const [yeild_date, setYeildDate] = useState(data.yeild_date);
  const [village, setVillage] = useState(data.village);
  const [crop, setCrop] = useState(data.crop);


  useEffect(() => {
    async function getData() {
      let response = await axios.get("http://localhost:4500/cropInfo/" + id);
      //  console.log(response.data)
      setData(response.data);
      setState(data[0].state)
      setDistrict(data[0].district)
      setTaluka(data[0].taluka)
      setArea(data[0].area)
      setSoil(data[0].soil)
      setSeason(data[0].season)
      setCultiDate(data[0].culti_date)
      setYeildDate(data[0].yeild_date)
      setVillage(data[0].village)
      setCrop(data[0].crop)
    }
    getData();
  }, []);

  console.log(data);
  console.log(state)

  
  async function updateData() {
    const data = {
      state: state,
      district: district,
      taluka: taluka,
      area: area,
      soil: soil,
      season: season,
      culti_date: culti_date,
      yeild_date: yeild_date,
      village: village,
      crop: crop,
    };
    console.log(data);
    const response = await axios.post("http://localhost:4500/savedata/", data);

    console.log(response.data);

    if (response.data === "sucess") {
      alert("project data saved sucessfully");
      window.location.reload();
    }
  }

  //   console.log(state)
  //   console.log(data[0].crop)

  console.log(crop)

  return (
    <>
      <Navbar />
      <div className="create-project" style={{ display: "flex" }}>
        <div className="part2">
          <div className="projectInfo">
            <div className="projectName">
              <div style={{ display: "flex" }}>
                <div>
                  {/* <input
                    type="text"
                    placeholder="Enter your Crop Name"
                    onChange={(e) => setCrop(e.target.value)}
                  /> */}

                  <select onChange={(e) => setCrop(e.target.value)} value={crop}>
                    <option>select crop</option>
                    <option>onion</option>
                    <option>sugarcane</option>
                    <option>cotton</option>
                    <option>oilseeds</option>
                    <option>jute</option>
                    <option>rice</option>
                    <option>soyabeans</option>
                    <option>coffee</option>
                    <option>Wheat</option>
                    <option>Tobacco</option>
                    <option>peanuts</option>
                    <option>Graps</option>
                  </select>
                </div>
                <button onClick={updateData}>Save Crop Data</button>
              </div>
            </div>

            <div className="selecttags1">
              <div>
                <p>state</p>

                <input
                  type="text"
                  placeholder="Enter Your State"
                 value={state}
                  onChange={(e) => setState(e.target.value)}
                />
              </div>

              <div>
                <p>district</p>
               
                <input
                  type="text"
                  placeholder="Enter yout District "
                 value={district}
                  onChange={(e) => setDistrict(e.target.value)}
                />
              </div>

              <div>
                <p>taluka</p>

                <input
                  type="text"
                   value={taluka}
                  placeholder="Enter your Taluka"
                  onChange={(e) => setTaluka(e.target.value)}
                />
              </div>
            </div>

            <div className="selecttags2">
              <div>
                <p>area</p>
                <input
                  type="number"
                  placeholder="enter crop area"
                 value={area}
                  onChange={(e) => setArea(e.target.value)}
                />
              </div>

              <div>
                <p>soil</p>
                <select onChange={(e) => setSoil(e.target.value)}>
                  <option>select soil type</option>
                  <option>sandy</option>
                  <option>Low</option>

                  <option>Medium</option>
                </select>
              </div>
              <div>
                <p>season</p>
                <select onChange={(e) => setSeason(e.target.value)}>
                  <option>select season</option>

                  <option>Summer</option>
                  <option>Winter</option>

                  <option>Quality</option>
                </select>
              </div>
            </div>

            <div className="selecttags3">
              <div>
                <p> cultivation date</p>
                <input
                  type="date"
                  placeholder="Enter Start Date"
                  onChange={(e) => setCultiDate(e.target.value)}
                />
              </div>
              <div>
                <p>expected yeild date</p>
                <input
                  type="date"
                  placeholder="Enter End Date"
                  onChange={(e) => setYeildDate(e.target.value)}
                />
              </div>
              <div>
                <p>village</p>

                <input
                  type="text"
                  placeholder="village"
                  onChange={(e) => setVillage(e.target.value)}
                  id="village"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default UpdateCropInfo;
