import React, { useState } from 'react'
import Data from './Data';
import { Link } from 'react-router-dom';

function Profile() {
let val=0;
let num=20;
let num2;
let farea;

let croptype;
let cropPhoto;
let billPhoto;

  const [area,setArea] = useState(1000);
  const [cropname,setcropName] = useState('onion');
  const [bill,setBill] = useState(null);
  const [imgcrop,setImgcrop] = useState(null);
  

  const [display ,setDisplay] = useState(false);

  function CollectArea(e){
 
    console.log(e.target.value);
    val=e.target.value;
   
    
   }
 

  function collectCropName(e){

    console.log(e.target.value);
   // cropname =e.target.value;
    setcropName(e.target.value)

  }
  function collectCropPhoto(e){

    console.log(e.target.value);
    setImgcrop(e.target.value)

  }
  function collectBill(e){

    console.log(e.target.value);
    setBill(e.target.value);

  }

  

  
  function handleSubmit(e){

     e.preventDefault();
     setDisplay(true);
     setArea(area-val)
  
  }

  return (
    <div profile-main>
    <div className='profile'>
      
        <div>

        </div>
    
    <div className='add-crop'>
       <h1>Add Crop</h1>
       <form onSubmit={handleSubmit}>
       <select onChange={collectCropName}>
            <option value='onion'>onion</option>
            <option value='graps'>graps</option>
            <option value='mango'>mango</option>
            <option value='banna'>banna</option>
            <option value='orange'>orange</option>
         </select>
         <br></br>
         
         <label>Upload gst bill for crop</label>
         <input type='file' onChange={collectBill}></input>
         <br></br>
         
         <label>Upload image of crop</label>
         <input type='file'onChange={collectCropPhoto}></input>
         <br></br>
         <label>Enter Area of crop aproximately in acers</label>
         <br></br>
        <input type='text' onChange={CollectArea} ></input>
        <br></br>  <br></br>
        
         <input type='submit' className='btn' ></input>
    
       </form>
       <h1>{
       display?area:null
      }</h1>


    
    </div>

    <Data 
      
farea={area}
croptype={cropname}
cropPhoto={imgcrop}
billPhoto={bill}
    ></Data>
   

    </div>
    </div>
  )
}

export default Profile;
