 import axios from "axios"
import React, { useEffect, useState } from "react";
// import SideComp from "./SideComp";
import "./AddData.css";
import moment from "moment";
import Navbar from "./Navbar/Navbar";

function AddData() {
  const [state, setState] = useState("");
  const [district, setDistrict] = useState("");
  const [taluka, setTaluka] = useState("");
  const [area, setArea] = useState("");
  const [soil,setSoil]=useState("")
  // const [crop, setCrop] = useState("");
  const [season, setSeason] = useState("");
  const [cultidate, setCultiDate] = useState("");
  const [yeilddate, setYeildDate] = useState("");
  const [village, setVillage] = useState("");
  const [crop, setCrop] = useState("");


   let distMaha=["nagar","pune","Beed"]
   let distKN=["kymbatur","pune","Beed"]

   let arr2=[]
   let arr =[

    {
      state1:"Maharashta",
      dist:["nagar","pune","Beed"]

    },
    {
      state1:"Karnatak",
      dist:["koyambatur","pune","Beed"]

    },

    {
      state1:"Gujarat",
      dist:["koyambatur","pune","Beed"]

    },
    {
      state1:"Karnatak",
      dist:["koyambatur","pune","Beed"]

    },
    {
      state1:"Karnatak",
      dist:["koyambatur","pune","Beed"]

    }

    
       
   ]

 

  async function save() {
    const data = {
     state:state,
     district:district,
     taluka:taluka,
     area:area,
     soil:soil,
     season:season,
     cultidate:cultidate,
     yeild:yeilddate,
     village:village,
     crop:crop

    };

    const response = await axios.post("http://localhost:4500/savedata/", data);

    console.log(response.data);

    if (response.data === "sucess") {
      alert("project data saved sucessfully");
      window.location.reload();
    }
  }

  const [id, setId] = useState();
  const [status, setStatus] = useState("");

  async function updateStatus() {
    const data = {
      id: id,
      status: status,
    };

    console.log(data);
  }

  // console.log(reason);
  // console.log(type);
  // console.log(division);
  // console.log(category);
  // console.log(startDate);
  // console.log(endDate);
  // console.log(department);
  // console.log(location);
  // console.log(priority);
  // console.log(category);

//   let now= moment()
//   console.log(now)
//  let formatedCurrentDate = now.format('YYYY-MM-DD')
//   console.log("curentDate--------------",formatedCurrentDate)
//   console.log("enddate-------------",endDate)



  // if(formatedCurrentDate<endDate){
  //   console.log("ok")
  // }
  // else{
  //   console.log("delay")
  // }

  return (
    <>
    <Navbar/>
    <div className="create-project" style={{ display: "flex" }}>
      
      <div className="part2">
        

        <div className="projectInfo">
          <div className="projectName">
            <div style={{ display: "flex" }}>
              <div>
                <input
                  type="text"
                  placeholder="Enter your project Theme"
                  onChange={(e) => setCrop(e.target.value)}
                />
              </div>
              <button onClick={save}>Save project</button>
            </div>
          </div>
          {/* 
      <div>
        <button onClick={save}>Save project</button>
      </div> */}

          <div className="selecttags1">
            {/* <label>reason</label>
            <label>type</label>
            <label>division</label> */}

           <div>
            <p>state</p>
           <select
              onChange={(e) => setState(e.target.value)}
              className="reason"
            >
              <option>select state</option>
              <option>Maharashta</option>
              <option>Gujarat</option>

              <option>Karnatak</option>
              <option>MadhyaPradesh</option>
              <option>Utter Pradesh</option>

            </select>
           </div>

            
             <div>
              <p>type</p>
             <select onChange={(e) => setDistrict(e.target.value)}>
                 
                 <option>select type</option>
                {/*<option>Latur</option>
                <option>chh. Sambhajinagar</option>
                <option>Ahilyanagar</option>
                <option>Pune</option>
                <option>Beed</option> */}
                {
                 arr.map((currValue)=>{
                  // console.log("in state")
                  if(currValue.state1==state){
                    
                    // console.log("in state")
                    // currValue.dist.map((items)=>{
                    //   console.log("in state")
                    //   return <option>{items}</option>
                      
                    // }  
                    // console.log(currValue.dist)
                    arr2 = currValue.dist
                   console.log("---------------",arr2)


                     
                  }
                 
                  
                 })
                   
                }

                {
                   arr2.length!=0 ?arr2.map((item)=>{
                      
                    return <option>{item}</option>
                   })  :null
                }



                
              </select>
             </div>
            
           <div>
            <p>divisions</p>
           <select onChange={(e) => setTaluka(e.target.value)}>
              <option>select division</option>
              <option>Compresser</option>
              <option>Filters</option>

              <option>Glass</option>
              <option>Pumps</option>
              <option>Water Heater</option>
            </select>
           </div>
          </div>

          <div className="selecttags2">
            {/* <label>reason</label>
            <label>type</label>
            <label>division</label>
            <br></br> */}
           <div>
            <p>category</p>
           <select onChange={(e) => setArea(e.target.value)}>
              <option>select category</option>
              <option>Ouality A</option>
              <option>Ouality B</option>

              <option>Ouality C</option>
              <option>Ouality D</option>
            </select>
            {/* <input type="text" placeholder="enter crop area" /> */}

           </div>

           <div>
            <p>priority</p>
           <select onChange={(e) => setSoil(e.target.value)}>
              <option>select priority</option>
              <option>High</option>
              <option>Low</option>

              <option>Medium</option>
            </select>
           </div>
          <div>
              <p>department</p>
          <select onChange={(e) => setSeason(e.target.value)}>
              <option>select department</option>

              <option>Startegy</option>
              <option>Finince</option>

              <option>Quality</option>
              <option>Maintance</option>
              <option>Stores</option>
              <option>HR</option>
            </select>
          </div>
          </div>
          {/* <select onChange={(e)=>setStartDate(e.target.value)}>
        
        </select> */}
          <div className="selecttags3">
           
           <div>
            <p>start date</p>
           <input
              type="date"
              placeholder="Enter Start Date"
              onChange={(e) => setCultiDate(e.target.value)}
            />
           </div>
           <div>
            <p>end date</p>
           <input
              type="date"
              placeholder="Enter End Date"
              onChange={(e) => setYeildDate(e.target.value)}
            />
           </div>
            <div>
              <p>location</p>
            <select onChange={(e) => setVillage(e.target.value)}>
              <option>select location</option>
              <option>Pune</option>
              <option>Mumbai</option>
              <option>Delhi</option>
            </select>
            </div>
          </div>
          <p className="reg">Status:Registered</p>
        </div>
      </div>
    </div>
    </>
  );
}

export default AddData;
