import axios from "axios";
import React, { useEffect, useState } from "react";
// import SideComp from "./SideComp";
import "./AddData.css";
import moment from "moment";
import Navbar from "./Navbar/Navbar";
import { useParams } from 'react-router-dom';

function UpdateCropInfo() {

    const { id } = useParams();
    console.log(id)

    const[data,setData]=useState([])

    useEffect(()=>{

       async function getData(){

          let response=await axios.get("http://localhost:4500/cropInfo/",id)
         console.log(response.data)
         setData(response.data)
        
        }
        getData()

    },[])

    

  const [state, setState] = useState("");
  const [district, setDistrict] = useState("");
  const [taluka, setTaluka] = useState("");
  const [area, setArea] = useState("");
  const [soil, setSoil] = useState("");
  // const [crop, setCrop] = useState("");
  const [season, setSeason] = useState("");
  const [culti_date, setCultiDate] = useState("");
  const [yeild_date, setYeildDate] = useState("");
  const [village, setVillage] = useState("");
  const [crop, setCrop] = useState("");

  
  async function updateData() {
    const data = {
      state: state,
      district: district,
      taluka: taluka,
      area: area,
      soil: soil,
      season: season,
      culti_date: culti_date,
      yeild_date: yeild_date,
      village: village,
      crop: crop,
    };
    console.log(data);
    const response = await axios.post("http://localhost:4500/savedata/", data);

    console.log(response.data);

    if (response.data === "sucess") {
      alert("project data saved sucessfully");
      window.location.reload();
    }
  }


  
  

  return (
    <>
      <Navbar />
      <div className="create-project" style={{ display: "flex" }}>
        <div className="part2">
          <div className="projectInfo">
            <div className="projectName">
              <div style={{ display: "flex" }}>
                <div>
                  {/* <input
                    type="text"
                    placeholder="Enter your Crop Name"
                    onChange={(e) => setCrop(e.target.value)}
                  /> */}

                  <select onChange={(e) => setCrop(e.target.value)}>
                    <option>onion</option>
                    <option>sugarcane</option>
                    <option>cotton</option>
                    <option>oilseeds</option>
                    <option>jute</option>
                    <option>rice</option>
                    <option>soyabeans</option>
                    <option>coffee</option>
                    <option>Wheat</option>
                    <option>Tobacco</option>
                    <option>peanuts</option>
                    <option>Graps</option>

                  </select>
                </div>
                <button onClick={updateData}>Save Crop Data</button>
              </div>
            </div>

            <div className="selecttags1">
              <div>
                <p>state</p>

                <input
                  type="text"
                  placeholder="Enter Your State"
                  onChange={(e) => setState(e.target.value)}
                />
              </div>

              <div>
                <p>district</p>

                <input
                  type="text"
                  placeholder="Enter yout District "
                  onChange={(e) => setDistrict(e.target.value)}
                />
              </div>

              <div>
                <p>taluka</p>

                <input
                  type="text"
                  placeholder="Enter your Taluka"
                  onChange={(e) => setTaluka(e.target.value)}
                />
              </div>
            </div>

            <div className="selecttags2">
              <div>
                <p>area</p>
                <input type="number" placeholder="enter crop area"   onChange={(e) => setArea(e.target.value)} />
              </div>

              <div>
                <p>soil</p>
                <select onChange={(e) => setSoil(e.target.value)}>
                  <option>select soil type</option>
                  <option>sandy</option>
                  <option>Low</option>

                  <option>Medium</option>
                </select>
              </div>
              <div>
                <p>season</p>
                <select onChange={(e) => setSeason(e.target.value)}>
                  <option>select season</option>

                  <option>Summer</option>
                  <option>Winter</option>

                  <option>Quality</option>
                </select>
              </div>
            </div>

            <div className="selecttags3">
              <div>
                <p> cultivation date</p>
                <input
                  type="date"
                  placeholder="Enter Start Date"
                  onChange={(e) => setCultiDate(e.target.value)}
                />
              </div>
              <div>
                <p>expected yeild date</p>
                <input
                  type="date"
                  placeholder="Enter End Date"
                  onChange={(e) => setYeildDate(e.target.value)}
                />
              </div>
              <div>
                <p>village</p>

                <input
                  type="text"
                  placeholder="village"
                  onChange={(e) => setVillage(e.target.value)}
                  id="village"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default UpdateCropInfo;
