import React from 'react'
import { useEffect,useState } from 'react';
import axios from 'axios';


function Admin() {


    const [data,setData] = useState([]);
let a =useEffect(()=>{

   
    axios.get("http://localhost:4500/")
     
   
    // .then(res=>console.log(res.data))
    .then(res=>setData(res.data))
    .catch(err=>console.log(err));
},[])

// data.map((a)=>{


  
// })
let name;
let pass;
const arr =[
  
    data.map((val)=>{
      
      val.name;
      val.password

    })
  
]

console.log(arr);
  return (
    <div>
      <main class="admin-data">
        <h1>User Info</h1>
        <table class="user-table" border={1}>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Mobile</th>
                    <th>Password</th>
                    <th>Confirmed Password</th>
                    <th>Farmer Image</th>
                    <th>7/12 Image</th>
                    <th>8A Image</th>
                   
                </tr>
            </thead>
           
             <tbody border>
             {
               
               data.map((farmer)=>{

                   return <tr>
                       {/* <td>{farmer.id}</td> */}
                       <td>{farmer.name}</td>
                       <td>{farmer.email}</td>
                       <td>{farmer.mobile}</td>
                       <td>{farmer.password}</td>
                       <td>{farmer.cpassword}</td>
                       <td>{farmer.fimg}</td>
                       <td>{farmer.seventimg}</td>
                       <td>{farmer.eightAimg}</td>
                       
                       {/* <td>
                           <button className="read-btn"><Link to={`/read/${student.id}`}>Read</Link></button> 
                           <button className="edit-btn"><Link to={`/edit/${student.id}`}>Edit</Link></button>
                           <button className="delete-btn"><Link onClick={()=>handleDelete(student.id)}>Delete</Link></button>
                       </td> */}
                        </tr>
               })
             }
            </tbody>

        </table>
    </main>
    </div>
  )
}

export default Admin
