import axios from 'axios';
import React, { useEffect, useState } from 'react'

// import "./ProjectList.css"
// import SideComp from './SideComp';

function AddData() {

  const [projectdata,setProjectData]=useState([]);

 async function fetchData(){

  const response = await axios.get("http://localhost:4500/projectdata/")
  console.log(response.data)
  setProjectData(response.data)

  }

  const [id,setId]=useState();
  const [status,setStatus]=useState("")

  async function updateStatus(e,id,status){

    e.preventDefault()
       const data={

        id:id,
        status:status

       }

       console.log(data)

       const response = await axios.post("http://localhost:4500/updatestatus/",data)

       console.log(response.data)
       window.location.reload()
       
  }

  useEffect(()=>{
    fetchData()
  },[])


  // pagination code
  const [currentPage, setCurrentPage] = useState(1);

  const recordPerPage = 7;

  const lastIndex = currentPage * recordPerPage;

  const firstIndex = lastIndex - recordPerPage;

  const records = projectdata.slice(firstIndex, lastIndex);

  const npage = Math.ceil(projectdata.length / recordPerPage);

  const numbers = [...Array(npage + 1).keys()].slice(1);

  function nextPage(){

    if(currentPage !== lastIndex){
        setCurrentPage(currentPage+1)
    }
  }

  function prePage(){

    if(currentPage !==firstIndex){

        setCurrentPage(currentPage-1)
    }
  }

  function changeCPage(event,id){

   event.preventDefault();
   setCurrentPage(id)
   console.log(id)

  }




console.log()
  return (
    <div style={{display:"flex"}} className='pojectList'>

     {/* <SideComp/> */}

     <div className='listheader'>
      <p>Project Listing</p>
      <div>

      </div>
     <div className='table'>
       

       <table>
         
         <thead>
           <th>Project Name</th>
           <th>Reason</th>
           <th>Type</th>
           <th>Division</th>
           <th>Category</th>
           <th>Priority</th>
           <th>Dept</th>
           <th>Loaction</th>
           <th>Status</th>
           <th></th>
 
         </thead>
 
         <tbody>
             
        {
         projectdata ?records.map((item)=>{
 
           return <tr>
           <td>{item.projectName}<br></br><label className='dates'>{item.startDate} to {item.endDate}</label></td>
           <td>{item.reason}</td>
           <td>{item.type}</td>
           <td>{item.division}</td>
           <td>{item.category}</td>
           <td>{item.priority}</td>
           <td>{item.department}</td>
           <td>{item.location}</td>
           <td>{item.status}</td>
           <td><button onClick={(e)=>updateStatus(e,item.id,"running")}>Start</button>
            <button onClick={(e)=>updateStatus(e,item.id,"Closed")}>Close</button> 
           <button onClick={(e)=>updateStatus(e,item.id,"Cancelled")}>cancel</button></td>
 
           </tr>
 
         }):null
        }
 
 
 
         </tbody>
 
       </table>
       
 
       <nav id='pagination'>
         <ul>
           <li>
             <a href="#" onClick={prePage}>
               prev
             </a>
           </li>
           {numbers.map((n) => {
             return (
               <li id="changePage">
                 <a  href="" 
                 onClick={(event)=> changeCPage(event,n)}>{n}</a>
               </li>
             );
           })}
          &nbsp;.&nbsp;.&nbsp;.&nbsp;
           <li>
             <a href="#" onClick={nextPage}>
               Next
             </a>
           </li>
         </ul>
       </nav>
 
       
     </div>
     </div>
    </div>
  )
}

export default AddData
